    "                if(not author == None):\n",
    "                    article['author'] = author.strip()\n",
    "                else:\n",
    "                    article['author'] = None\n",
    "            \n",
    "                # time posted\n",
    "                posted = otherInfo.find('time', class_=\"content-header__publish-date\")\n",
