    "            if(p.attrs == {} and len(p.text.strip())>0):\n",
    "                # only want 'p'\n",
    "                writtenContent = writtenContent + prefix +  p.text.strip()\n",
    "                prefix = \" \"\n",
    "        article['body'] = writtenContent\n",
