    "        lists = content.find_all('ul')\n",
    "        listElements = []\n",
    "        # increment through the lists\n",
    "        for l in lists:\n",
    "            nextList = l.find_all('li')\n",
    "            for element in nextList:\n",
    "                listElements.append(element.text)\n",
    "        if(len(listElements)==0):\n",
    "            article['list'] = None\n",
    "        else:\n",
    "            article['list'] = listElements\n",
    "\n",
